const get = (key, value) => {
  const data = JSON.parse(localStorage.getItem(key));
  return data === null ? value : data;
};

const set = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const clear = () => {
  localStorage.clear();
};

const objectExport = {
  get: get,
  set: set,
  clear: clear,
};
export default objectExport;
