//llamada al API

const getDataFromAPI = () => {
  return fetch("https://rickandmortyapi.com/api/character")
    .then((response) => response.json())
    .then((data) => {
      const cleanData = data.results.map((results) => {
        return {
          id: results.id,
          name: results.name,
          specie: results.species,
          image: results.image,
        };
      });
      return cleanData;
    });
};

export default getDataFromAPI;
